    it('should take the same amount of time comparing different string sizes', function (done) {

        var now = Date.now();
        Cryptiles.fixedTimeComparison(b, a);
        var t1 = Date.now() - now;

        now = Date.now();
        Cryptiles.fixedTimeComparison(b, b);
        var t2 = Date.now() - now;

        expect(t2 - t1).to.be.within(-20, 20);
