    it('maxSockets on default agents can be changed', function (done) {

        var complete;

        var server = Http.createServer(function (req, res) {

            res.writeHead(200);
            res.write('foo');

            complete = complete || function () {

                res.end();
            };
        });

        server.listen(0, function () {

            Wreck.agents.http.maxSockets = 1;

